mapper.update:
update shgoodsstock


  STATE = #{state,jdbcType=INTEGER},


    MEMO = #{memo,jdbcType=VARCHAR},


where 1 = 1

  and UUID = #{uuid,jdbcType=VARCHAR}


  and GOODSUUID = #{goodsUuid,jdbcType=VARCHAR}


  and NO = #{no,jdbcType=INTEGER}

mapper.delete:
delete from shgoodsstock
where 1 = 1

  and UUID = #{uuid,jdbcType=VARCHAR}


  and GOODSUUID = #{goodsUuid,jdbcType=VARCHAR}


  and NO = #{no,jdbcType=INTEGER}

mapper.select:
select

from shgoodsstock
where 1 = 1

  and UUID = #{uuid,jdbcType=VARCHAR}


  and GOODSUUID = #{goodsUuid,jdbcType=VARCHAR}


  and NO = #{no,jdbcType=INTEGER}
